import React, { useEffect, useState } from 'react';
import Board from './components/board';
import Controls from './components/controls';
import { getGameState, aiMove, opponentMove, resetGame } from './api';

const App = () => {
    const [board, setBoard] = useState([]);
    const [turn, setTurn] = useState('');
    const [gameStatus, setGameStatus] = useState('');
    const [selectedCell, setSelectedCell] = useState(null);

    const fetchGameState = async () => {
        try {
            const data = await getGameState();
            console.log('Fetched game state:', data);
            setBoard(data.board);
            setTurn(data.turn);
            setGameStatus(data.game_status);
            setSelectedCell(null);
        } catch (error) {
            console.error('Error fetching game state:', error);
            alert('Failed to fetch game state.');
        }
    };

    useEffect(() => {
        fetchGameState();
    }, []);

    useEffect(() => {
    console.log('Turn updated:', turn);
}, [turn]);

    const handleCellClick = async (row, col) => {
        if (gameStatus === 'over') {
            alert('Game is over.');
            return;
        }

        if (turn !== 'Opponent') {
            alert("It's AI's turn!");
            return;
        }

        const totalPieces = board.flat().filter(cell => cell !== '').length;
        const dropPhase = totalPieces < 8;

        if (dropPhase) {
            const move = [[row, col]];
            try {
                console.log('Sending opponent drop move:', move);
                const data = await opponentMove(move);
                console.log('fetched handle cell click', data)
                setBoard(data.board);
                setGameStatus(data.game_status);
                setTurn(data.turn);
            } catch (error) {
                console.error('Error with drop move:', error);
                alert(error.response?.data?.detail || 'Invalid move.');
            }
        } else {
            if (!selectedCell) {
                setSelectedCell([row, col]);
            } else {
                const move = [selectedCell, [row, col]];
                try {
                    console.log('Sending opponent move:', move);
                    const data = await opponentMove(move);
                    setBoard(data.board);
                    setGameStatus(data.game_status);
                    setTurn(data.turn);
                } catch (error) {
                    console.error('Error with move:', error);
                    alert(error.response?.data?.detail || 'Invalid move.');
                } finally {
                    setSelectedCell(null);
                }
            }
        }
    };

    const handleAIMove = async () => {
        if (gameStatus === 'over') {
            alert('Game is over.');
            return;
        }

        if (turn !== 'AI') {
            alert("It's not AI's turn!");
            return;
        }

        try {
            const response = await aiMove();
            console.log('Raw response from handle AI move', response)
            console.log('current turn', data.turn)
            setBoard(data.board);
            setGameStatus(data.game_status);
            setTurn(data.turn);
        } catch (error) {
            console.error('Error with AI move:', error);
            alert('Failed to process AI move.');
        }
    };

    const handleReset = async () => {
        try {
            await resetGame();
            fetchGameState();
        } catch (error) {
            console.error('Error resetting game:', error);
            alert('Failed to reset game.');
        }
    };

    return (
        <div className="App">
            <h1>Teeko Game</h1>
            <h2>Turn: {turn}</h2>
            <h3>Status: {gameStatus === 'over' ? 'Game Over' : 'In Progress'}</h3>
            <Board board={board} onCellClick={handleCellClick}/>
            <Controls onAIMove={handleAIMove} onReset={handleReset}/>
        </div>
    );
};

export default App;
