import React, { useEffect, useState } from 'react';
import Board from './components/board';
import Controls from './components/controls';
import { getGameState, aiMove, opponentMove, resetGame } from './api';

const App = () => {
    const [board, setBoard] = useState([]);
    const [turn, setTurn] = useState('');
    const [gameStatus, setGameStatus] = useState('');
    const [selectedCell, setSelectedCell] = useState(null);

    // Fetch initial game state
    const fetchGameState = async () => {
        try {
            console.log('Fetching game state...');
            const data = await getGameState();
            console.log('Game state fetched:', data);
            setBoard(data.board);
            setTurn(data.turn); // "AI" or "Opponent"
            setGameStatus(data.game_status); // "ongoing" or "over"
        } catch (error) {
            console.error('Error fetching game state:', error);
            alert('Failed to fetch game state. Please try again.');
        }
    };

    useEffect(() => {
        fetchGameState();
    }, []);

    const handleCellClick = async (row, col) => {
    if (gameStatus === 'over') {
        alert('Game is over. Please reset to start a new game.');
        return;
    }

    if (turn !== 'Opponent') {
        alert("It's the opponent's turn!");
        return;
    }

    const totalPieces = board.flat().filter(cell => cell !== '').length;
    const dropPhase = totalPieces < 8; // Fewer than 8 pieces => Drop Phase

    const handleMoveError = (error) => {
        console.error('Error with move:', error);
        alert(error.response?.data?.detail || 'Error making move. Please try again.');
    };

    if (dropPhase) {
        // Drop phase: Only one click is needed
        const move = [[row, col]];
        try {
            console.log('Sending opponent drop move:', move);
            const data = await opponentMove(move);
            setBoard(data.board);
            setGameStatus(data.game_status);
            setTurn(data.turn);
        } catch (error) {
            handleMoveError(error);
        }
    } else {
        // Move phase: Two clicks required
        if (!selectedCell) {
            console.log(`Cell selected at row: ${row}, col: ${col}`);
            setSelectedCell([row, col]);
        } else {
            const move = [selectedCell, [row, col]];
            try {
                console.log('Sending opponent move:', move);
                const data = await opponentMove(move);
                setBoard(data.board);
                setGameStatus(data.game_status);
                setTurn(data.turn);
            } catch (error) {
                handleMoveError(error);
            } finally {
                setSelectedCell(null);
            }
        }
    }
};


    const handleAIMove = async () => {
        if (gameStatus === 'over') {
            alert('Game is over. Please reset to start a new game.');
            return;
        }

        if (turn !== 'AI') {
            alert('It is not AI\'s turn!');
            return;
        }

        try {
            console.log('Requesting AI move...');
            const data = await aiMove(); // Make AI move via API
            console.log('AI move result:', data);

            // Update state with the data returned by the API
            setBoard(data.board);
            setGameStatus(data.game_status);
            setTurn(data.turn);
        } catch (error) {
            console.error('Error with AI move:', error);
            alert('Failed to process AI move. Please try again.');
        }
    };

    const handleReset = async () => {
        try {
            console.log('Resetting game...');
            await resetGame();
            setSelectedCell(null); // Clear selected cell
            fetchGameState(); // Fetch initial state after reset
        } catch (error) {
            console.error('Error resetting game:', error);
            alert('Failed to reset game. Please try again.');
        }
    };

    return (
        <div className="App">
            <h1>Teeko Game</h1>
            <h2>Turn: {turn}</h2>
            <h3>Status: {gameStatus === 'over' ? 'Game Over' : 'In Progress'}</h3>
            <Board board={board} onCellClick={handleCellClick} />
            <Controls onAIMove={handleAIMove} onReset={handleReset} />
        </div>
    );
};

export default App;
