import axios from 'axios';

const API_URL = 'http://127.0.0.1:8000'; // Your FastAPI backend URL

const axiosInstance = axios.create({
    baseURL: API_URL,
    headers: {
        'Content-Type': 'application/json',
    },
});

export const getGameState = async () => {
    const response = await axios.get(`${API_URL}/`);
    return response.data;
};

export const aiMove = async () => {
    const response = await axios.post(`${API_URL}/ai-move/`);
    return response.data;
};

export const opponentMove = async (move) => {
    const response = await axios.post(`${API_URL}/opponent-move/`, { move });
    return response.data;
};

export const resetGame = async () => {
    const response = await axios.post(`${API_URL}/reset/`);
    return response.data;
};
