import React, {useEffect, useState} from 'react';
import Board from './components/board';
import Controls from './components/controls';
import {getGameState, aiMove, opponentMove, resetGame} from './api';

const App = () => {
    const [board, setBoard] = useState([]);
    const [turn, setTurn] = useState('');
    const [gameStatus, setGameStatus] = useState('');
    const [selectedCell, setSelectedCell] = useState(null);

    const fetchGameState = async () => {
        try {
            const data = await getGameState();
            console.log('Fetched game state:', data);
            setBoard(data.board);
            setTurn(data.turn);
            setGameStatus(data.game_status);
            setSelectedCell(null);
        } catch (error) {
            console.error('Error fetching game state:', error);
            alert('Failed to fetch game state.');
        }
    };

    useEffect(() => {
        fetchGameState();
    }, []);

    useEffect(() => {
    }, [turn]);

    const handleCellClick = async (row, col) => {
        if (gameStatus === 'over') {
            alert('Game is over.');
            return;
        }

        if (turn !== 'Opponent') {
            alert("It's AI's turn!");
            return;
        }

        // Calculate if it's the drop phase
        const totalPieces = board.flat().filter(cell => cell !== ' ' && cell !== '').length;
        const dropPhase = totalPieces < 8;

        console.log(`Clicked cell: [${row}, ${col}], Drop Phase: ${dropPhase}`);

        if (dropPhase) {
            // Handle drop phase: Single cell selection
            const move = [[row, col]];

            try {
                const data = await opponentMove(move);
                setBoard(data.board);
                setGameStatus(data.game_status);
                setTurn(data.turn);

            } catch (error) {
                console.error('Error with drop phase move:', error);
                alert(error.response?.data?.detail || 'Invalid move.');
            }
        } else {
            // Handle move phase: Two cell selection
            if (!selectedCell) {
                // Validate that the selected cell contains the opponent's piece
                if (board[row][col] !== 'r') {
                    alert('You must select one of your pieces to move.');
                    return;
                }
                setSelectedCell([row, col]); // First click selects the source
                console.log('Selected Source Cell:', [row, col]);
            } else {
                const move = [selectedCell, [row, col]]; // Second click selects the destination
                console.log('Move Phase Move:', move);

                try {
                    console.log("Move being sent to backend:", move);
                    console.log("Source cell:", selectedCell, "Destination cell:", [row, col]);

                    const data = await opponentMove(move); // Send move to backend
                    console.log('Move phase response:', data);
                    setBoard(data.board);
                    setGameStatus(data.game_status);
                    setTurn(data.turn);

                } catch (error) {
                    console.error('Error with move phase move:', error);
                    alert(error.response?.data?.detail || 'Invalid move.');
                } finally {
                    setSelectedCell(null); // Clear the selected cell
                }
            }
        }
    };

    const handleAIMove = async () => {
        if (gameStatus === 'over') {
            alert('Game is over.');
            return;
        }

        if (turn !== 'AI') {
            alert("It's not AI's turn!");
            return;
        }

        try {
            const data = await aiMove();
            console.log('Fetched data from handle AI move:', data);
            console.log('Current turn:', data.turn);
            setBoard(data.board);
            setGameStatus(data.game_status);
            setTurn(data.turn);

        } catch (error) {
            console.error('Error with AI move:', error);
            alert('Failed to process AI move.');
        }
    };

    const handleReset = async () => {
        try {
            await resetGame();
            fetchGameState();
        } catch (error) {
            console.error('Error resetting game:', error);
            alert('Failed to reset game.');
        }
    };

    return (
        <div className="App">
            <h1>Teeko Game</h1>
            <h2>Turn: {turn}</h2>
            <h3>Status: {gameStatus === 'over' ? 'Game Over' : 'In Progress'}</h3>
            <Board
                board={board}
                onCellClick={handleCellClick}
                dropPhase={board.flat().filter(cell => cell !== '').length < 8}
            />
            <Controls onAIMove={handleAIMove} onReset={handleReset}/>
        </div>
    );
};

export default App;
