import React, { useEffect, useState } from 'react';
import Board from './components/board';
import Controls from './components/controls';
import { getGameState, aiMove, opponentMove, resetGame } from './api';

const App = () => {
    const [board, setBoard] = useState([]);
    const [turn, setTurn] = useState('');
    const [gameStatus, setGameStatus] = useState('');
    const [selectedCell, setSelectedCell] = useState(null);

    // Fetch initial game state
    const fetchGameState = async () => {
        try {
            console.log('Fetching game state...');
            const data = await getGameState();
            console.log('Game state fetched:', data);
            setBoard(data.board);
            setTurn(data.turn);
            setGameStatus(data.game_status);
        } catch (error) {
            console.error('Error fetching game state:', error);
            alert('Failed to fetch game state. Check the console for details.');
        }
    };

    useEffect(() => {
        fetchGameState();
    }, []);

    const handleCellClick = async (row, col) => {
        if (gameStatus === 'over') {
            alert('Game is over. Reset to start a new game.');
            return;
        }

        if (!selectedCell) {
            console.log(`Cell selected at row: ${row}, col: ${col}`);
            setSelectedCell([row, col]);
        } else {
            try {
                const move = [selectedCell, [row, col]];
                console.log('Sending opponent move:', move);
                const data = await opponentMove(move);
                console.log('Opponent move result:', data);
                setBoard(data.board);
                setGameStatus(data.game_status);
                setTurn(data.turn);
            } catch (error) {
                console.error('Error with opponent move:', error);
                alert(error.response?.data?.detail || 'Error making move. Check console.');
            } finally {
                setSelectedCell(null);
            }
        }
    };

    const handleAIMove = async () => {
        if (gameStatus === 'over') {
            alert('Game is over. Reset to start a new game.');
            return;
        }

        try {
            console.log('Requesting AI move...');
            const data = await aiMove();
            console.log('AI move result:', data);
            setBoard(data.board);
            setGameStatus(data.game_status);
            setTurn(data.turn);
        } catch (error) {
            console.error('Error with AI move:', error);
            alert('Failed to process AI move. Check the console.');
        }
    };
    const handleReset = async () => {
    try {
        console.log('Resetting game...');
        const resetResponse = await resetGame();
        console.log('Response from reset API:', resetResponse);

        console.log('Fetching updated game state...');
        const newGameState = await getGameState();

        console.log('Updated game state:', newGameState);

        // Update state with the new game state
        setBoard(newGameState.board);
        setGameStatus(newGameState.game_status);
        setTurn(newGameState.turn);
        setSelectedCell(null);

        // Debugging board update
        console.log('Updated board state:', newGameState.board);
    } catch (error) {
        console.error('Error resetting game:', error);
        alert('Failed to reset game. Check console for details.');
    }
};

    return (
        <div className="App">
            <h1>Teeko Game</h1>
            <h2>Turn: {turn}</h2>
            <h3>Status: {gameStatus}</h3>
            <Board board={board} onCellClick={handleCellClick} />
            <Controls onAIMove={handleAIMove} onReset={handleReset} />
        </div>
    );
};

export default App;
